import { HttpClient } from '@angular/common/http';
import { EventEmitter, Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { FoodList } from '../module/food-list';

@Injectable({
  providedIn: 'root'
})
export class FoodListService {
  public emitEvent = new EventEmitter()
  private list: Array<string> = [
    "arros",
    "feijao",
    "ovos"
  ]
  private url: string = "http://localhost:3000/"
  constructor(private http: HttpClient) { }
  // public foodList() {
  //   return this.list
  // }
  public foodList(): Observable<FoodList> {
    return this.http.get<FoodList>(`${this.url}list-food`).pipe(
      res => res,
      error => error
      )
  }

  public foodListAdd (value: string): Observable<FoodList> {
    return this.http.post<FoodList>(`${this.url}list-food`, { nome: value }).pipe(
      res => res,
      error => error
      )
  }
  public foodListedit (id: number): Observable<FoodList> {
    return this.http.post<FoodList>(`${this.url}list-food${id}`, { nome: value }).pipe(
      res => res,
      error => error
      )
  }
  public foodListAlert(value: FoodList) {
    return this.emitEvent.emit(value)
  }
}
